{
  "name": "Welcome New Lead",
  "coachId": "{{coach_id}}",
  "triggerEvent": "lead_created",
  "triggerCondition": {
    "field": "source",
    "operator": "equals",
    "value": "website"
  },
  "actions": [
    {
      "type": "send_whatsapp_message",
      "config": {
        "message": "Welcome! Thank you for your interest in our coaching program.",
        "templateId": "welcome_template"
      }
    },
    {
      "type": "add_lead_tag",
      "config": {
        "tag": "new_lead"
      }
    },
    {
      "type": "add_to_funnel",
      "config": {
        "funnelId": "onboarding_funnel",
        "stageId": "welcome_stage"
      }
    }
  ]
}

{
  "name": "Lead Status Follow Up",
  "coachId": "{{coach_id}}",
  "triggerEvent": "lead_status_changed",
  "triggerCondition": {
    "field": "status",
    "operator": "equals",
    "value": "qualified"
  },
  "actions": [
    {
      "type": "send_whatsapp_message",
      "config": {
        "message": "Great! You've been qualified as a potential client. Let's schedule a call.",
        "templateId": "qualified_lead_template"
      }
    },
    {
      "type": "create_task",
      "config": {
        "title": "Follow up with qualified lead",
        "description": "Contact qualified lead within 24 hours",
        "assignee": "{{coach_id}}",
        "dueDate": "{{current_date_plus_1_day}}"
      }
    },
    {
      "type": "add_lead_tag",
      "config": {
        "tag": "qualified"
      }
    }
  ]
}

{
  "name": "Hot Lead Alert",
  "coachId": "{{coach_id}}",
  "triggerEvent": "lead_temperature_changed",
  "triggerCondition": {
    "field": "temperature",
    "operator": "equals",
    "value": "hot"
  },
  "actions": [
    {
      "type": "send_internal_notification",
      "config": {
        "message": "üî• HOT LEAD ALERT: {{lead_name}} just became a hot lead!",
        "recipients": ["{{coach_id}}", "sales_team"]
      }
    },
    {
      "type": "create_task",
      "config": {
        "title": "URGENT: Contact Hot Lead",
        "description": "Immediate follow-up required for hot lead",
        "assignee": "{{coach_id}}",
        "dueDate": "{{current_time_plus_30_minutes}}"
      }
    },
    {
      "type": "update_lead_score",
      "config": {
        "score": 90,
        "reason": "Lead temperature changed to hot"
      }
    }
  ]
}

{
  "name": "Form Submission Nurturing",
  "coachId": "{{coach_id}}",
  "triggerEvent": "form_submitted",
  "triggerCondition": {
    "field": "form_type",
    "operator": "equals",
    "value": "contact_form"
  },
  "actions": [
    {
      "type": "send_whatsapp_message",
      "config": {
        "message": "Thanks for reaching out! We'll get back to you within 2 hours.",
        "templateId": "form_submission_acknowledgment"
      }
    },
    {
      "type": "schedule_drip_sequence",
      "config": {
        "sequenceId": "form_submission_sequence",
        "delay": 3600
      }
    },
    {
      "type": "add_lead_tag",
      "config": {
        "tag": "form_submitted"
      }
    },
    {
      "type": "create_task",
      "config": {
        "title": "Follow up form submission",
        "description": "Contact lead who submitted form",
        "assignee": "{{coach_id}}",
        "dueDate": "{{current_time_plus_2_hours}}"
      }
    }
  ]
}

{
  "name": "Appointment Booking Confirmation",
  "coachId": "{{coach_id}}",
  "triggerEvent": "appointment_booked",
  "actions": [
    {
      "type": "send_whatsapp_message",
      "config": {
        "message": "Your appointment has been confirmed for {{appointment_date}} at {{appointment_time}}. We'll send a reminder 24 hours before.",
        "templateId": "appointment_confirmation"
      }
    },
    {
      "type": "create_calendar_event",
      "config": {
        "title": "Coaching Session with {{client_name}}",
        "startTime": "{{appointment_start_time}}",
        "endTime": "{{appointment_end_time}}",
        "attendees": ["{{client_email}}"]
      }
    },
    {
      "type": "add_note_to_lead",
      "config": {
        "note": "Appointment booked for {{appointment_date}} at {{appointment_time}}",
        "noteType": "appointment"
      }
    },
    {
      "type": "add_lead_tag",
      "config": {
        "tag": "appointment_scheduled"
      }
    }
  ]
}

{
  "name": "Payment Success Activation",
  "coachId": "{{coach_id}}",
  "triggerEvent": "payment_successful",
  "actions": [
    {
      "type": "send_whatsapp_message",
      "config": {
        "message": "üéâ Payment successful! Your access has been activated. Welcome to our coaching program!",
        "templateId": "payment_success"
      }
    },
    {
      "type": "add_lead_tag",
      "config": {
        "tag": "paid_customer"
      }
    },
    {
      "type": "move_to_funnel_stage",
      "config": {
        "funnelId": "onboarding_funnel",
        "stageId": "welcome_stage"
      }
    },
    {
      "type": "create_invoice",
      "config": {
        "amount": "{{payment_amount}}",
        "description": "Coaching program payment",
        "dueDate": "{{payment_date}}"
      }
    },
    {
      "type": "create_task",
      "config": {
        "title": "Onboard new customer",
        "description": "Welcome call and program introduction",
        "assignee": "{{coach_id}}",
        "dueDate": "{{current_date_plus_1_day}}"
      }
    }
  ]
}

{
  "name": "Funnel Progress Tracking",
  "coachId": "{{coach_id}}",
  "triggerEvent": "funnel_stage_entered",
  "triggerCondition": {
    "field": "stage_name",
    "operator": "equals",
    "value": "consideration"
  },
  "actions": [
    {
      "type": "send_whatsapp_message",
      "config": {
        "message": "Great progress! You're now in the consideration stage. Here's what's next...",
        "templateId": "funnel_progress_template"
      }
    },
    {
      "type": "add_note_to_lead",
      "config": {
        "note": "Entered consideration stage in {{funnel_name}}",
        "noteType": "funnel_progress"
      }
    },
    {
      "type": "update_lead_score",
      "config": {
        "score": 60,
        "reason": "Advanced to consideration stage"
      }
    }
  ]
}

{
  "name": "Appointment Reschedule Handling",
  "coachId": "{{coach_id}}",
  "triggerEvent": "appointment_rescheduled",
  "actions": [
    {
      "type": "send_whatsapp_message",
      "config": {
        "message": "Your appointment has been rescheduled to {{new_appointment_date}} at {{new_appointment_time}}.",
        "templateId": "appointment_rescheduled"
      }
    },
    {
      "type": "add_note_to_lead",
      "config": {
        "note": "Appointment rescheduled from {{old_date}} to {{new_date}}",
        "noteType": "appointment_change"
      }
    },
    {
      "type": "create_task",
      "config": {
        "title": "Update calendar for rescheduled appointment",
        "description": "Update calendar event for {{client_name}}",
        "assignee": "{{coach_id}}",
        "dueDate": "{{current_time_plus_1_hour}}"
      }
    }
  ]
}

{
  "name": "Appointment Cancellation Follow Up",
  "coachId": "{{coach_id}}",
  "triggerEvent": "appointment_cancelled",
  "actions": [
    {
      "type": "send_whatsapp_message",
      "config": {
        "message": "We're sorry to hear you had to cancel. Let's reschedule when you're ready!",
        "templateId": "appointment_cancelled"
      }
    },
    {
      "type": "add_note_to_lead",
      "config": {
        "note": "Appointment cancelled for {{appointment_date}} - Reason: {{cancellation_reason}}",
        "noteType": "appointment_cancellation"
      }
    },
    {
      "type": "create_task",
      "config": {
        "title": "Follow up on cancelled appointment",
        "description": "Contact {{client_name}} to reschedule",
        "assignee": "{{coach_id}}",
        "dueDate": "{{current_date_plus_2_days}}"
      }
    },
    {
      "type": "add_followup_date",
      "config": {
        "followupDate": "{{current_date_plus_1_week}}",
        "notes": "Check if client wants to reschedule"
      }
    }
  ]
}

{
  "name": "Payment Failure Recovery",
  "coachId": "{{coach_id}}",
  "triggerEvent": "payment_failed",
  "actions": [
    {
      "type": "send_whatsapp_message",
      "config": {
        "message": "We encountered an issue with your payment. Please update your payment method.",
        "templateId": "payment_failed"
      }
    },
    {
      "type": "add_lead_tag",
      "config": {
        "tag": "payment_issue"
      }
    },
    {
      "type": "create_task",
      "config": {
        "title": "Follow up on failed payment",
        "description": "Contact {{client_name}} about payment issue",
        "assignee": "{{coach_id}}",
        "dueDate": "{{current_time_plus_2_hours}}"
      }
    },
    {
      "type": "add_followup_date",
      "config": {
        "followupDate": "{{current_date_plus_3_days}}",
        "notes": "Retry payment or offer alternative payment methods"
      }
    }
  ]
}

{
  "name": "Subscription Cancellation Retention",
  "coachId": "{{coach_id}}",
  "triggerEvent": "subscription_cancelled",
  "actions": [
    {
      "type": "send_whatsapp_message",
      "config": {
        "message": "We're sorry to see you go! Is there anything we can do to keep you?",
        "templateId": "subscription_cancelled"
      }
    },
    {
      "type": "add_lead_tag",
      "config": {
        "tag": "churned_customer"
      }
    },
    {
      "type": "create_task",
      "config": {
        "title": "Retention call for cancelled subscription",
        "description": "Call {{client_name}} to understand cancellation reason",
        "assignee": "{{coach_id}}",
        "dueDate": "{{current_time_plus_1_hour}}"
      }
    },
    {
      "type": "schedule_drip_sequence",
      "config": {
        "sequenceId": "retention_sequence",
        "delay": 86400
      }
    }
  ]
}

{
  "name": "Task Overdue Escalation",
  "coachId": "{{coach_id}}",
  "triggerEvent": "task_overdue",
  "actions": [
    {
      "type": "send_internal_notification",
      "config": {
        "message": "‚ö†Ô∏è OVERDUE TASK: {{task_title}} is overdue by {{overdue_days}} days",
        "recipients": ["{{task_assignee}}", "{{coach_id}}"]
      }
    },
    {
      "type": "create_task",
      "config": {
        "title": "Follow up on overdue task",
        "description": "Check status of overdue task: {{task_title}}",
        "assignee": "{{coach_id}}",
        "dueDate": "{{current_time_plus_1_hour}}"
      }
    },
    {
      "type": "add_note_to_lead",
      "config": {
        "note": "Task '{{task_title}}' is overdue - requires immediate attention",
        "noteType": "system_alert"
      }
    }
  ]
}

{
  "name": "Lead Data Management",
  "coachId": "{{coach_id}}",
  "triggerEvent": "lead_created",
  "actions": [
    {
      "type": "update_lead_score",
      "config": {
        "score": 75,
        "reason": "Initial lead score based on source"
      }
    },
    {
      "type": "add_lead_tag",
      "config": {
        "tag": "high_value_lead"
      }
    },
    {
      "type": "update_lead_field",
      "config": {
        "field": "priority",
        "value": "high"
      }
    },
    {
      "type": "create_deal",
      "config": {
        "dealName": "Coaching Program - {{lead_name}}",
        "amount": 5000,
        "stage": "prospecting"
      }
    }
  ]
}

{
  "name": "Multi-Channel Communication",
  "coachId": "{{coach_id}}",
  "triggerEvent": "lead_created",
  "actions": [
    {
      "type": "send_whatsapp_message",
      "config": {
        "message": "Welcome! Here's your exclusive access link: {{access_link}}",
        "templateId": "welcome_template"
      }
    },
    {
      "type": "create_email_message",
      "config": {
        "subject": "Welcome to Our Coaching Program",
        "body": "Dear {{lead_name}}, welcome to our exclusive coaching program...",
        "templateId": "welcome_email_template"
      }
    },
    {
      "type": "send_push_notification",
      "config": {
        "title": "Welcome! üéâ",
        "body": "Your coaching journey starts now!",
        "data": {
          "type": "welcome",
          "action": "open_app"
        }
      }
    },
    {
      "type": "schedule_drip_sequence",
      "config": {
        "sequenceId": "welcome_sequence",
        "delay": 3600
      }
    }
  ]
}

{
  "name": "Workflow Automation",
  "coachId": "{{coach_id}}",
  "triggerEvent": "lead_created",
  "actions": [
    {
      "type": "create_task",
      "config": {
        "title": "Initial contact with {{lead_name}}",
        "description": "Make first contact within 24 hours",
        "assignee": "{{coach_id}}",
        "dueDate": "{{current_date_plus_1_day}}"
      }
    },
    {
      "type": "create_calendar_event",
      "config": {
        "title": "Follow-up call with {{lead_name}}",
        "startTime": "{{current_time_plus_2_days}}",
        "endTime": "{{current_time_plus_2_days_plus_30_minutes}}",
        "attendees": ["{{lead_email}}"]
      }
    },
    {
      "type": "add_note_to_lead",
      "config": {
        "note": "Lead created from {{source}} - Initial assessment needed",
        "noteType": "system"
      }
    },
    {
      "type": "add_followup_date",
      "config": {
        "followupDate": "{{current_date_plus_3_days}}",
        "notes": "Check lead engagement and interest level"
      }
    }
  ]
}

{
  "name": "Zoom Meeting Automation",
  "coachId": "{{coach_id}}",
  "triggerEvent": "appointment_booked",
  "actions": [
    {
      "type": "create_zoom_meeting",
      "config": {
        "topic": "Coaching Session with {{client_name}}",
        "startTime": "{{appointment_start_time}}",
        "duration": 60,
        "attendees": ["{{client_email}}"]
      }
    },
    {
      "type": "send_whatsapp_message",
      "config": {
        "message": "Your Zoom meeting link: {{zoom_meeting_link}}",
        "templateId": "zoom_meeting_link"
      }
    }
  ]
}

{
  "name": "Payment Processing Automation",
  "coachId": "{{coach_id}}",
  "triggerEvent": "payment_successful",
  "actions": [
    {
      "type": "create_invoice",
      "config": {
        "amount": "{{payment_amount}}",
        "description": "Coaching Program - {{program_name}}",
        "dueDate": "{{payment_date}}"
      }
    },
    {
      "type": "send_whatsapp_message",
      "config": {
        "message": "Invoice #{{invoice_number}} has been generated for your payment.",
        "templateId": "invoice_generated"
      }
    }
  ]
}

{
  "name": "System Integration Automation",
  "coachId": "{{coach_id}}",
  "triggerEvent": "lead_created",
  "actions": [
    {
      "type": "call_webhook",
      "config": {
        "url": "https://external-crm.com/api/leads",
        "method": "POST",
        "headers": {
          "Authorization": "Bearer {{external_api_token}}",
          "Content-Type": "application/json"
        },
        "payload": {
          "name": "{{lead_name}}",
          "email": "{{lead_email}}",
          "source": "{{lead_source}}"
        }
      }
    },
    {
      "type": "trigger_another_automation",
      "config": {
        "automationRuleId": "{{welcome_sequence_rule_id}}",
        "delay": 300
      }
    }
  ]
}

{
  "name": "Lead Scoring Example",
  "coachId": "{{coach_id}}",
  "triggerEvent": "content_consumed",
  "actions": [
    {
      "type": "update_lead_score",
      "config": {
        "score": 85,
        "reason": "Engaged with premium content"
      }
    }
  ]
}

{
  "name": "Lead Tagging Example",
  "coachId": "{{coach_id}}",
  "triggerEvent": "lead_created",
  "actions": [
    {
      "type": "add_lead_tag",
      "config": {
        "tag": "vip_client"
      }
    },
    {
      "type": "remove_lead_tag",
      "config": {
        "tag": "cold_lead"
      }
    }
  ]
}

