  "name": "Funnel Management Example",
  "coachId": "{{coach_id}}",
  "triggerEvent": "lead_converted_to_client",
  "actions": [
    {
      "type": "add_to_funnel",
      "config": {
        "funnelId": "customer_onboarding",
        "stageId": "welcome_stage"
      }
    },
    {
      "type": "move_to_funnel_stage",
      "config": {
        "funnelId": "sales_funnel",
        "stageId": "closed_won"
      }
    },
    {
      "type": "remove_from_funnel",
      "config": {
        "funnelId": "prospect_funnel"
      }
    }
  ]
}

{
  "name": "Lead Field Update Example",
  "coachId": "{{coach_id}}",
  "triggerEvent": "lead_status_changed",
  "actions": [
    {
      "type": "update_lead_field",
      "config": {
        "field": "priority",
        "value": "high"
      }
    },
    {
      "type": "create_deal",
      "config": {
        "dealName": "Coaching Package - {{lead_name}}",
        "amount": 2500,
        "stage": "negotiation"
      }
    }
  ]
}

{
  "name": "Communication Actions Example",
  "coachId": "{{coach_id}}",
  "triggerEvent": "appointment_reminder_time",
  "actions": [
    {
      "type": "send_whatsapp_message",
      "config": {
        "message": "Reminder: Your appointment is in 1 hour!",
        "templateId": "appointment_reminder"
      }
    },
    {
      "type": "create_email_message",
      "config": {
        "subject": "Appointment Reminder - {{appointment_date}}",
        "body": "This is a reminder about your upcoming appointment...",
        "templateId": "appointment_reminder_email"
      }
    },
    {
      "type": "send_internal_notification",
      "config": {
        "message": "Appointment reminder sent to {{client_name}}",
        "recipients": ["{{coach_id}}"]
      }
    },
    {
      "type": "send_push_notification",
      "config": {
        "title": "Appointment Reminder",
        "body": "Your appointment starts in 1 hour",
        "data": {
          "type": "appointment_reminder",
          "appointment_id": "{{appointment_id}}"
        }
      }
    }
  ]
}

{
  "name": "Drip Sequence Example",
  "coachId": "{{coach_id}}",
  "triggerEvent": "form_submitted",
  "actions": [
    {
      "type": "schedule_drip_sequence",
      "config": {
        "sequenceId": "welcome_series",
        "delay": 0
      }
    }
  ]
}

{
  "name": "Task Management Example",
  "coachId": "{{coach_id}}",
  "triggerEvent": "lead_created",
  "actions": [
    {
      "type": "create_task",
      "config": {
        "title": "Initial contact with {{lead_name}}",
        "description": "Make first contact and assess needs",
        "assignee": "{{coach_id}}",
        "dueDate": "{{current_date_plus_1_day}}"
      }
    },
    {
      "type": "add_note_to_lead",
      "config": {
        "note": "Lead created from {{source}} - Initial assessment pending",
        "noteType": "system"
      }
    },
    {
      "type": "add_followup_date",
      "config": {
        "followupDate": "{{current_date_plus_3_days}}",
        "notes": "Check engagement level and interest"
      }
    }
  ]
}

{
  "name": "Calendar Event Example",
  "coachId": "{{coach_id}}",
  "triggerEvent": "appointment_booked",
  "actions": [
    {
      "type": "create_calendar_event",
      "config": {
        "title": "Coaching Session - {{client_name}}",
        "startTime": "{{appointment_start_time}}",
        "endTime": "{{appointment_end_time}}",
        "attendees": ["{{client_email}}"]
      }
    }
  ]
}

{
  "name": "Payment Processing Example",
  "coachId": "{{coach_id}}",
  "triggerEvent": "payment_successful",
  "actions": [
    {
      "type": "create_invoice",
      "config": {
        "amount": "{{payment_amount}}",
        "description": "Coaching Program Payment",
        "dueDate": "{{payment_date}}"
      }
    },
    {
      "type": "issue_refund",
      "config": {
        "amount": 500,
        "reason": "Partial refund for early cancellation",
        "paymentId": "{{original_payment_id}}"
      }
    }
  ]
}

{
  "name": "System Integration Example",
  "coachId": "{{coach_id}}",
  "triggerEvent": "lead_converted_to_client",
  "actions": [
    {
      "type": "call_webhook",
      "config": {
        "url": "https://crm.example.com/api/leads/convert",
        "method": "POST",
        "headers": {
          "Authorization": "Bearer {{crm_api_token}}",
          "Content-Type": "application/json"
